
$src='G:\'; $dest='.\Tools\testtest'; $exe='.\Tools\LRCFB.exe'; $root=(Resolve-Path $src).Path.TrimEnd('\'); Get-ChildItem -Path $root -Recurse -File -Filter *.rcf | ForEach-Object { $rel=$_.DirectoryName.Substring($root.Length).TrimStart('\'); $out=Join-Path (Join-Path $dest $rel) ($_.BaseName + '_rcf'); New-Item -ItemType Directory -Force -Path $out | Out-Null; & $exe -inputrcf $_.FullName -outputdir $out }
